import * as React from 'react';
import {
    View,
    Text,
    ScrollView,
    FlatList
} from 'react-native';
import firebase from 'firebase';
import db from '../config';

export default class SearchScreen extends React.Component{
    constructor(props){
        super(props)
        this.state={
            allTransactions:[]
        }
    }
    componentDidMount=async()=>{
        const queries = await db.collection('Transaction').get()
        queries.docs.map((doc)=>{
            this.setState({
                allTransactions:[this.state.allTransactions, doc.data()]
            })
        })
    }
    render(){
        return(
            <ScrollView>
                {this.state.allTransactions.map((transaction)=>{
                    return(
                        <View style={{borderWidth:1.4, borderRadius:1.4}}>
                            <Text>{"Book ID:- " + transaction.BookID}</Text>
                            <Text>{"Student ID:- " + transaction.StudentID}</Text>
                            <Text>{"Transaction Type:- " + transactios.Transactiontype}</Text>
                            <Text>{"Transaction Date:- " + transactions.date.toDate()}</Text>
                        </View>
                    );
                })
                }
            </ScrollView>
        );
    }
}
